insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('huckleberry-gin','Huckleberry Gin','New Western Gin','Deutschland','Schwarzwald',44,'Heidelbeere, Angelikawurzeln, Koriandersamen, Ingwerwurzeln, Zimt, Lavendel, Zitronenschalen, Holunderblüten, Hopfenblüten, Pfefferminze, 12 weitere Botanicals','Beere, fruchtig, süß','Dieser Gin wird in Handarbeit dreifach aus 22 Botanicals destilliert. Für den gintypischen Geschmack bilden Wacholderbeeren, Angelikawurzeln, Koriandersamen, Ingwerwurzeln, etwas Zimt und ein Hauch von Lavendel die Basis. Die fruchtigen Noten werden Zitronenschalen, Holunder- und Hopfenblüten, Pfefferminze und Himbeeren entnommen. Bei der Herstellung kommen nur natürliche Gewürze und frische Beeren in den Kessel. Das heißt, keine Aromen oder Geschmacksverstärker. Im letzten Destillationsschritt rundet das Hauptbotanical, die Heidelbeere, den Gin ab und gibt ihm seinen einzigartigen Geschmack.',1,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('skin-gin','Skin Gin','New Western Gin','Deutschland','Altes Land bei Hamburg',42,'Marokkanische Minze, Orange, Pink Grapefruit, Limetten, Zitrone, Koriander','Minze, süß','Neben klassischem Wacholder spielt bei Skin Gin marokkanische Minze die Hauptrolle. Die handverlesenen Botanicals werden einzeln in traditionellem Verfahren destilliert. Auf diese Weise werden nahezu 100% der feinen Essenzen gewonnen, die sich im Skin Gin vereinen. Aber auch das Flaschendesign ist besonders. Skin Gin durchbricht die Schwere von alkohol- und wacholderlastiger Gins und begeistert mit einer fantastisch intensiven und frischen Minznote. Den Skin Gin gibt es in vielen unterschiedlichen, ausgefallenen und handgefertigten Design-Editionen. Für jeden gibt es also den richtigen ´Skin´.',1,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('sturzflug-premium-dry-gin','Sturzflug Premium Dry Gin','New Western Gin','Deutschland','Rosenberg auf der Ostalb',44,'Ingwer, Kardamom, Koriander, Kubeben-Pfeffer, Limetten, Orange, Paradieskörner, Tonkabohne, Wacholder, Zitrone','Zitrus, klassisch','Die Sturzflug Gins, die in der Brennerei des Landgasthof Adlers destilliert werden, werden je nach Nachfrage in kleinen Batches und in echter Handarbeit hergestellt. Von der Auswahl der Ingredienzen über die Destillation bis hin zur Abfüllung in die Flaschen und dem Aufbringen der Etiketten. Das raue und herbe Klima der rauen Ostalb bildet die perfekte Grundlage für dieses Destillat. Im Sturzflug Gin finden sich insgesamt fünfzehn handverlesene Botanicals in Premiumqualität, die ihm seinen charateristischen zitrusfrischen Geschmack verleihen.',0,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('mandarina-dry-gin','Mandarina Dry Gin','Dry Gin','Deutschland','Kriftel',41,'Blutorangen, Grapefruit, Ingwer, Limetten, Mandarine, Orange, Süßholzwurzel, Thymian, Wacholder, Zitronengras, Zitronenmelisse','fruchtig','Die Mandarine ist Programm und präsentiert sich wunderbar balanciert in jeder Facette ohne die anderen Aromen zu übertönen. Eingebettet in einem Potpourri von hesperiden Aromen mit herber Fruchtsüße macht jeder pure Schluck Lust auf noch mehr von diesem außergewöhnlichen Gin. Die Wacholderaromen spielen hier eine untergeordnete Rolle, der Star ist hier eindeutig der gelungene Mix aus Zitrusfrüchten der Extraklasse. Pur ein weiches, sinnliches Erlebnis, als Gin Tonic der absolute Frischekick!',0,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('mirabeau-dry-rose-gin','Mirabeau Dry Rosé Gin','Dry Gin','Frankreich','Merpins',43,'Engelwurz, Iriswurzel, Jasmin, Koriander, Lavendelblüte, Lorbeeren, Rosenblüten, Rosmarin, Thymian, Wacholder, Zitrone','','Im Grunde ist Mirabeau Dry Gin ein klassischer London (Dry) Gin. Und doch ist hier alles ein wenig anders. Dem 100%igen Traubenalkohol, der als Basisalkohol verwendet wird, verdankt er seinen weichen und zartfruchtigen Gaumen. Zitronen und Koriander verleihen ihm die feine Zitrusfrische, Iriswurzeln und Engelwurz leicht blumige und erdige Noten, unterlegt mit den floralen Anklängen der nur sparsam zugesetzten Blüten von Rosen, Jasmin und Lavendel. Lorbeer, Thymian und Rosmarin vollenden die Aromenpalette mit typisch provenzalischen Kräuternoten.',0,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('momotaro-akainu','Momotarō Gin Akainu','New Western Gin','Deutschland','Westhofen in Rheinland-Pfalz',42,'Amaou-Erdbeere, Matcha, Wacholder, Yamazakura-Kirsche, Yuzu','fruchtig','Der Name Momotarō, auf deutsch „Pfirsichjunge“, entstammt einer beliebten japanischen Sage. Der Legende nach findet ein kinderloses Paar im Fluss einen ungewöhnlich großen Momo-Pfirsich, aus diesem schlüpfte ein Junge, den sie auf den Namen Momotarō tauften.',0,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('quarantini-social-dry-gin','Quarantini - Social Dry Gin','Dry Gin','Deutschland','Frankfurt',42,'Bergamotte, Papaya, Pomeranze, Wacholder','fruchtig','Quarantäne ist nichts anderes, als ein großer Stubenarrest. Die Hersteller sind aber Optimisten, und haben das Beste aus dieser Situation gemacht und tun damit gleichzeitig etwas Gutes. Quarantini ist der erste „Social Dry Gin“ mit dem in diesen Zeiten der Gastronomie und dem lokalen inhabergeführten Einzelhandel etwas zurück gegeben wird.',0,1);
insert into `gins` (`id`, `name`, `type`, `origin_country`, `origin_city`, `alcohol_content`, `botanicals`, `main_notes`, `description`, `is_tipp`, `is_public` ) values ('la-su-mango-gin','LA SU Mango Gin','New Western Gin','Deutschland','Altenstadt',43,'Lavendel, Mango, Orangenzesten, Sternanis, Wacholder','fruchtig','Exotischer Gin mit einer weichen Mango-Note, abgerundet durch verschiedene Botanicals wie Sternanis, Lavendel, Kardamon, Orangenzesten, Wacholder und weiteren geheimen Ingredienzen.',0,1);
